export const ADD_NOTE = "ADD_NOTE";

export const addNote = (title, description) => {
    return {
        type: ADD_NOTE,
        payload: {
            id: Date.now(), // Adaugă un ID unic aici
            title: title, 
            description: description
        }
    };
};


export const DELETE_NOTE = 'DELETE_NOTE';

export const deleteNote = (index) => {
    return {
        type: DELETE_NOTE,
        payload: {index}
    }
}
;
