import React from "react";

const Note = (props) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(title);
  const [editDescription, setEditDescription] = useState(description);
  const dispatch = useDispatch();

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    dispatch(updateNote(id, editTitle, editDescription));
    setIsEditing(false);
  };

  return (
    <div
      style={{
        width: "240px",
        height: "240px",
        background: "#f5b042",
        borderRadius: "10px",
        overflowY: "auto",
        margin: "4px",
        padding: "10px",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
      }}
    >
      <h3>{props.title}</h3>
      <span style={{ marginTop: "8px" }}>{props.description}</span>
        <button onClick={() => props.onDelete(props.id)} 
          style={{ marginTop: "8px",
          backgroundColor: "#ff4d4d"}}>
          Delete Note
        </button>
        <button onClick={() => props.onUpdate(props.id, props.title, props.description)} 
        style={{ marginTop: "8px",
        backgroundColor: "#32de84"}}>
        Update note</button>
    </div>
  );
};

export default Note;
